import React from 'react';
import { Marker, NaverMap } from 'react-naver-maps';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      center: { y: 37.45061506754004, x: 127.13004659924906 },
    };

  }





  render() {
    const navermaps = window.naver.maps;

    return (
      <div>
        <NaverMap
          id="maps-getting-started-controlled"
          style={{ width: '80%', height: '600px' }}
          defaultZoom={15}
          center={this.state.center}
          onCenterChange={(center) => this.setState({ center })}
        >
          <Marker
            key={2}
            position={new navermaps.LatLng(36.350833, 127.385)}
            animation={0}
            // Marker 클릭시 동작
          />
        </NaverMap>
      </div>
    );
  }
}

export default App;
