import React,{useState} from "react";
import { MapMarker, Map } from "react-kakao-maps-sdk";

export const markerdata = [
  {
      title: "콜드스퀘어",
      lat: 37.62197524055062,
      lng: 127.16017523675508
  },
  {
      title: "오륙도",
      lat: 35.0938,
      lng: 129.1264
  },
  {
      title: "울산대학교",
      lat: 35.5441,
      lng: 129.2563
  },
  {
      title: "대전 오월드",
      lat: 36.2887,
      lng: 127.3978
  },
  {
      title: "광주공항",
      lat: 35.1399,
      lng: 126.811
  },
  {
      title: "경포대 해변",
      lat: 37.8055,
      lng: 128.9078
  }
];
const App = () => {
  const [isOpen, setIsOpen] = useState(false)
    return (
      <Map center={{ lat: 37.365264512305174, lng: 127.10676860117488 }} style={{ width: "600px", height: "600px" }} level={5}>
      {markerdata.map((position, index) => (
          <>
              <MapMarker
                  key={position.title}
                  position={{ lat: position.lat, lng: position.lng }}
                  image={{
                      src: "https://t1.daumcdn.net/localimg/localimages/07/mapapidoc/markerStar.png", // 마커이미지의 주소입니다
                      size: {
                          widht: 24,
                          height: 35
                      } // 마커이미지의 크기입니다
                  }}
                  clickable={true}
                  onclick={()=> setIsOpen(true)}
                  >
                    {isOpen &&(
                      <div style = {{ minWidth: "150px" }}>
                      <img
                        alt="close"
                        width="14"
                        height="13"

                        style={{
                          position: "absolute",
                          right: "5px",
                          top: "5px",
                          cursor: "pointer",
                        }}
                        onclick={()=> setIsOpen(false)}
                        />
                        <div style={{padding: "5px", color: "#000" }}>markerdata.title</div>
                        /</div>
                    )}
                  </MapMarker>
          </>
      ))}
  </Map>
    );
};

export default App;
